// Add a helper function to check Supabase connection
export const checkSupabaseConnection = async () => {
  try {
    const { data, error } = await supabase.from('notifications').select('count').limit(1);
    if (error) {
      console.error("Supabase connection error:", error);
      return false;
    }
    console.log("Supabase connection successful");
    return true;
  } catch (err) {
    console.error("Failed to check Supabase connection:", err);
    return false;
  }
};

// Helper function to create notifications for different events
export const createNotification = async (
  userId: string | null,
  type: string, 
  title: string, 
  description?: string, 
  actionUrl?: string
) => {
  if (!userId) return null;
  
  try {
    console.log(`[createNotification] Creating ${type} notification for user ${userId}`);
    
    const notification = {
      user_id: userId,
      type,
      title,
      description: description || '',
      action_url: actionUrl || '',
      read: false
    };
    
    const { data, error } = await supabase
      .from('notifications')
      .insert(notification)
      .select()
      .single();
      
    if (error) {
      console.error("[createNotification] Error:", error);
      return null;
    }
    
    console.log("[createNotification] Created successfully:", data);
    return data;
  } catch (err) {
    console.error("[createNotification] Exception:", err);
    return null;
  }
};

// Helper function to mark messages as read
export const markMessagesAsRead = async (
  receiverId: string,
  senderId: string
): Promise<boolean> => {
  try {
    const now = new Date().toISOString();
    console.log(`[markMessagesAsRead] Marking messages as read from ${senderId} to ${receiverId} at ${now}`);
    
    const { error } = await supabase
      .from('messages')
      .update({ read_at: now })
      .eq('receiver_id', receiverId)
      .eq('sender_id', senderId)
      .is('read_at', null);
      
    if (error) {
      console.error('[markMessagesAsRead] Failed to mark messages as read:', error);
      return false;
    }
    
    console.log(`[markMessagesAsRead] Successfully marked messages as read at ${now}`);
    return true;
  } catch (err) {
    console.error('[markMessagesAsRead] Exception:', err);
    return false;
  }
};

// Helper function to mark a single message as read
export const markMessageAsRead = async (messageId: string): Promise<boolean> => {
  try {
    const now = new Date().toISOString();
    console.log(`[markMessageAsRead] Marking message ${messageId} as read at ${now}`);
    
    const { error } = await supabase
      .from('messages')
      .update({ read_at: now })
      .eq('id', messageId)
      .is('read_at', null);
      
    if (error) {
      console.error('[markMessageAsRead] Failed to mark message as read:', error);
      return false;
    }
    
    console.log(`[markMessageAsRead] Successfully marked message ${messageId} as read at ${now}`);
    return true;
  } catch (err) {
    console.error('[markMessageAsRead] Exception:', err);
    return false;
  }
};
